import React, { useState, useEffect } from "react";
import styles from "./Post.module.css";

const Post = props => {
  const selectorHandler = () => {
    const messageCount = props.entry.split(" ");
    if (messageCount.length <= 40) return;

    props.postSelector(props.entry);
  };
  const summaryPost = (open, currentPost, message, count) => {
    const newBody = message.split(" ");
    if (
      (open && currentPost === message && window.innerWidth > 599) ||
      newBody.length <= count
    ) {
      return message;
    }

    const lastEl = newBody[count - 1].split("").slice(-1)[0];
    const punctuation = [".", "!", ",", "?", ";", ":", "&"];

    if (punctuation.indexOf(lastEl) !== -1)
      return `${newBody.slice(0, count + 1).join(" ")}...`;

    return `${newBody.slice(0, count).join(" ")}...`;
  };

  console.log(styles.Mobile);

  return (
    <>
      <hr />
      <div className={styles.Post} onClick={() => selectorHandler()}>
        {summaryPost(props.open, props.currentPost, props.entry, 40)}
      </div>
    </>
  );
};

export default Post;
