import React, { useState } from "react";
import styles from "./Posts.module.css";
import { useSelector } from "react-redux";
import Post from "../../components/Post/Post";
import Modal from "../../components/UI/Modal/Modal";

const Posts = () => {
  const posts = useSelector(state => state.posts);
  const [open, setOpen] = useState(false);
  const [currentPost, setCurrentPost] = useState(null);
  const openHandler = () => setOpen(oldState => !oldState);
  const postSelector = post => {
    setCurrentPost(post);
    openHandler();
  };

  const body = (count, message) => {
    const newBody = message.split(" ");
    if (newBody.length < count) {
      return newBody.join(" ");
    }
    const lastEl = newBody[count - 1].split("").slice(-1)[0];
    const punctuation = [".", "!", ",", "?", ";", ":", "&"];
    if (punctuation.indexOf(lastEl) !== -1) {
      return `${newBody.slice(0, count + 1).join(" ")}...`;
    }
    return `${newBody.slice(0, count).join(" ")}...`;
  };

  return (
    <>
      <Modal open={open} openHandler={openHandler}>
        {currentPost !== null ? body(25, currentPost) : null}
      </Modal>
      <div className={styles.Posts}>
        {posts.reverse().map(post => (
          <Post entry={post.message} postSelector={postSelector} />
        ))}
      </div>
    </>
  );
};

export default Posts;
