import React, { useState } from "react";
import styles from "./Posts.module.css";
import { useSelector } from "react-redux";
import Post from "../../components/Post/Post";
import PostModal from "../../components/UI/PostModal/PostModal";

const Posts = () => {
  const posts = useSelector(state => state.posts);
  const [open, setOpen] = useState(false);
  const [currentPost, setCurrentPost] = useState(null);
  const [postCount, setPostCount] = useState(25);
  const openHandler = () => setOpen(oldState => !oldState);
  const postSelector = post => {
    setCurrentPost(post);
    openHandler();
    setPostCount(null);
  };

  return (
    <>
      <PostModal open={open} openHandler={openHandler}>
        {currentPost}
      </PostModal>
      <div className={styles.Posts}>
        {posts.reverse().map(post => (
          <Post
            entry={post.message}
            postSelector={postSelector}
            count={postCount}
          />
        ))}
      </div>
    </>
  );
};

export default Posts;
