import * as actionTypes from "./actionTypes";

export const fetchMessages = (skip, count) => {
  return async (dispatch) => {
    try {
      dispatch(fetchMessagesStart());
      const response = await fetch(
        `/api/messages?count=${count}&skip=${skip}`,
        {
          method: "GET",
          mode: "cors",
          cache: "no-cache",
          credentials: "same-origin",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      const data = await response.json();
      const { messages, couint } = data;

      dispatch(fetchMessagesSuccess(messages, count));
    } catch (err) {
      dispatch(fetchMessagesFail(err));
    }
  };
};

export const fetchMessagesStart = () => {
  return {
    type: actionTypes.FETCH_MESSAGES_START,
  };
};

export const fetchMessagesSuccess = (messages, count) => {
  return {
    type: actionTypes.FETCH_MESSAGES_SUCCESS,
    messages,
    count,
  };
};

export const fetchMessagesFail = (messages) => {
  return {
    type: actionTypes.FETCH_MESSAGES_SUCCESS,
    messages,
  };
};
