import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import parse from "html-react-parser";
import styles from "./FullPost.module.css";
import { setPost, resetPost } from "../../store/actions";

const FullPost = props => {
  const post = useSelector(state => state.currentPost);
  const dispatch = useDispatch();

  useEffect(() => {
    const searchParams = new URLSearchParams(props.location.search);
    const postId = searchParams.get("id");
    dispatch(setPost(postId));
    return () => dispatch(resetPost());
  }, [props.location.search, dispatch]);

  let postBody = null;

  if (post) postBody = parse(post.message);

  return <div>{postBody}</div>;
};

export default FullPost;
