import { useEffect, useReducer } from "react";
import {
  fetchMessagesReset,
  resetMessage,
  getPersonsReset,
} from "../../store/actions/index";

import { useSelector, useDispatch } from "react-redux";

const initialState = {
  error: null,
  customerHandler: null,
  resetError: null,
};

const errorReducer = (errorState, action) => {
  switch (action.type) {
    case "persons":
      return {
        ...errorState,
        error: action.error,
        customerHandler: action.handler,
        resetError: getPersonsReset,
      };
    case "submitMessage":
      return {
        ...errorState,
        error: action.error,
        customerHandler: action.handler,
        resetError: resetMessage,
      };
    case "message":
      return {
        ...errorState,
        error: action.error,
        customerHandler: action.handler,
        resetError: resetMessage,
      };
    case "messages":
      return {
        ...errorState,
        error: action.error,
        customerHandler: action.handler,
        resetError: fetchMessagesReset,
      };
    default:
      return initialState;
  }
};

const useErrorHandler = (error, customerHandler = null) => {
  const [errorState, dispatchError] = useReducer(errorReducer, initialState);
  const messagesError = useSelector((state) => state.messages.error);
  const messageError = useSelector((state) => state.message.error);
  const personsError = useSelector((state) => state.persons.error);
  const body = customerHandler !== null ? customerHandler : error;

  //   if (messageError) {
  //     body = "bingo bango bongo";
  //   }
  //   const [hasError, setHasError] = useState(false);
  const dispatch = useDispatch();

  useEffect(() => {
    if (error === "persons") {
      dispatchError({ type: personsError, action: customerHandler });
    } else if (error === "messages") {
      dispatchError({ type: messagesError, action: customerHandler });
    } else if (error === "message") {
      dispatchError({ type: messageError, action: customerHandler });
    } else if (error === "submitMessage") {
    }
  }, [errorState.error]);

  return { body };
};

export default useErrorHandler;
