import React from "react";

const LoadMore = (props) => {
  let loadMoreMessages = null;

  if (props.filteredMessages.length > 0) {
    loadMoreMessages = (count) => {
      const marker =
        props.messages.length - props.messagesCutoff >= count
          ? count
          : props.messages.length - props.messagesCutoff;
      props.setMessagesCutoff((prevState) => prevState + marker);
    };
  }

  const loadMoreFiltered = (count) => {
    const marker =
      props.filteredMessages.length - props.filteredCutoff >= count
        ? count
        : props.filteredMessages.length - props.filteredCutoff;
    props.setFilteredCutoff((prevState) => prevState + marker);
  };

  const loadMessages =
    props.messages.length - props.messagesCutoff > 0 ? (
      <div onClick={() => loadMoreMessages(14)}>LOAD MORE</div>
    ) : null;

  const loadFiltered =
    props.filteredMessages.length - props.filteredCutoff > 0 ? (
      <div onClick={() => loadMoreFiltered(14)}>LOAD MORE</div>
    ) : null;

  return <>{loadMessages}</>;
};

export default LoadMore;
