import React, { useState, useEffect, useReducer } from "react";
import { useSelector, useDispatch } from "react-redux";

const messagesError = useSelector((state) => state.messages.error);
const messageError = useSelector((state) => state.message.error);
const personsError = useSelector((state) => state.persons.error);
const [errorState, dispatchError] = useReducer(errorReducer, initialState);
// import {
//   fetchMessagesReset,
//   resetMessage,
//   getPersonsReset,
// } from "../store/actions/index";

const initialState = {
  error: null,
  customerHandler: null,
  resetError: null,
};

const errorReducer = (errorState, action) => {
  switch (action.type) {
    case "persons":
      return {
        ...errorState,
        error: personsError,
      };
    case "submitMessage":
      return {
        ...errorState,
        error: messageError,
      };
    case "message":
      return {
        ...errorState,
        error: messageError,
      };
    case "messages":
      return {
        ...errorState,
      };
    default:
      break;
  }
};

const useErrorHandler = (error, customerHandler = null) => {
  let body = null;

  if (messageError) {
    body = "bingo bango bongo";
  }
  //   const [hasError, setHasError] = useState(false);
  const dispatch = useDispatch();

  useEffect(() => {
    if (errorState.error === null) {
      dispatchError({ type: error });
    }
  }, [errorState.error]);

  return body;
};

export default useErrorHandler;
