// import { faFilter } from "@fortawesome/free-solid-svg-icons";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import { addFilter, removeFilter } from "../../store/actions";
import { useDispatch } from "react-redux";

const FilterOptions = props => {
  // const filterHandler = (selected, filter, name) => {
  //   if (selected) {
  //     dispatch(addFilter(filter, name));
  //   }
  //   dispatch(removeFilter(filter, name));
  // };

  let classes = [styles.FilterOptions];

  if (props.expand) {
    classes = [styles.FilterOptions, styles.OpenFilterOptions].join(" ");
  }

  const whoFilters = [
    "Grandmother",
    "Grandfather",
    "Mother",
    "Father",
    "Son",
    "Daughter",
    "Grandson",
    "Granddaughter",
    "Sister",
    "Brother",
    "Cousin",
    "Friend",
    "Husband",
    "Wife",
    "Coworker",
    "Classmate",
    "Neighbor",
    "Other"
  ].map(person => (
    <FilterOption filterHandler={filterHandler} filter={person} name="who" />
  ));

  return (
    <div className={styles.FilterOption}>
      <div className={classes}>
        <Container>
          <Row>
            <Col lg={4}>
              <Form>{whoFilters}</Form>
            </Col>
            <Col lg={4}>2</Col>
            <Col lg={4}>3</Col>
          </Row>
        </Container>
      </div>
    </div>
  );
};

export default FilterOptions;
