import React, { useEffect, Suspense } from "react";
import { Route, Switch } from "react-router-dom";
import { getPersons, fetchMessages } from "../../store/actions";
import { useDispatch } from "react-redux";
// import { AnimatedSwitch } from "react-router-transition";
import Spinner from "../../components/UI/Spinners/FormSpinner/FormSpinner";
import ContentContainer from "../ContentContainer/ContentContainer";
// import styles from "./Cockpit.module.css";

const Landing = React.lazy(() => import("../../components/Landing/Landing"));
const FullMessage = React.lazy(() =>
  import("../../components/FullMessage/FullMessage")
);

// const mapStyles = (styles) => {
//   return {
//     opacity: styles.opacity,
//     transform: `translateX(${styles.offset}%)`,
//   };
// };

// const animatedTransition = {
//   atEnter: {
//     opacity: 0,
//     offset: -100,
//   },
//   // leave in a transparent, downscaled state
//   atLeave: {
//     opacity: 0,
//     offset: -100,
//   },
//   // and rest at an opaque, normally-scaled state
//   atActive: {
//     opacity: 1,
//     offset: 0,
//   },
// };

const Cockpit = (props) => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getPersons());
    dispatch(fetchMessages());
  }, [dispatch]);
  let cockpitBody = (
    <Suspense fallback={<Spinner />}>
      <Switch>
        <Route exact path="/message/:id">
          <ContentContainer>
            <FullMessage {...props} />
          </ContentContainer>
        </Route>
        <Route exact path="/">
          <Landing {...props} />
        </Route>
      </Switch>
    </Suspense>
  );
  //Landing
  //Resources
  //About
  //Message

  return cockpitBody;
};

export default Cockpit;
