  const [open, setOpen] = useState(false);
  const openHandler = () => setOpen(oldState => !oldState);

  return (
    <>
      <Modal open={open}>Hi</Modal>;
      <h1>If you were here, I wish I could tell you...</h1>
      <div className={styles.TextBox} onClick={props.modalOpen}>
        Enter Here <span className={styles.TypeWriter}>|</span>
      </div>
