import React, { useState, useEffect } from "react";
import Button from "../UI/Buttons/Button/Button";
import styles from "./Message.module.css";
import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";

const Message = props => {
  const [messageAnswer, setMessageAnswer] = useState(null);

  useEffect(() => setMessageAnswer(props.messageValue), [props.messageValue]);

  return (
    <>
      <h3>I am a ...</h3>
      <div className={styles.messageAnswer}>{messageAnswer}</div>
      <div className={styles.MessageContent}>
        <Form onChange={event => setMessageAnswer(event.target.value)}>
          <Form.Group controlId="exampleForm.ControlInput1">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="name@example.com" />
          </Form.Group>
          <Form.Group controlId="exampleForm.ControlSelect1">
            <Form.Label>Example select</Form.Label>
            <Form.Control as="select">
              <option>1</option>
              <option>2</option>
              <option>3</option>
              <option>4</option>
              <option>5</option>
            </Form.Control>
          </Form.Group>
          <Form.Group controlId="exampleForm.ControlSelect2">
            <Form.Label>Example multiple select</Form.Label>
            <Form.Control as="select" multiple>
              <option>1</option>
              <option>2</option>
              <option>3</option>
              <option>4</option>
              <option>5</option>
            </Form.Control>
          </Form.Group>
          <Form.Group controlId="exampleForm.ControlTextarea1">
            <Form.Label>Example textarea</Form.Label>
            <Form.Control as="textarea" rows="3" />
          </Form.Group>
        </Form>
        ;
        <Button
          type="prev"
          switchHandler={props.switchHandler}
          name="message"
          formValue={messageAnswer}
        />
      </div>
    </>
  );
};

export default Message;
