import React, { useState, useEffect } from "react";
import styles from "./FilterOption.module.css";
import { useDispatch, useSelector } from "react-redux";
import { addFilter, removeFilter } from "../../../store/actions";

const FilterOption = props => {
  const [selected, setSelected] = useState(false);
  const { who, identity } = useSelector(state => state.filters);
  const sort = useSelector(state => state.sort);
  const dispatch = useDispatch();
  const { filter, filterHandler, filterSelected, name } = props;
  const selectHandler = (filter, name) => {
    // if (filterSelected && filter !== filterSelected) {
    //   // return;
    //   setSelected(prevState => !prevState);
    // }
    setSelected(prevState => !prevState);
    filterHandler(filter, name);
  };

  useEffect(() => {
    //we need to check to see if the content is filtered-- to determine which array of posts to sort
    //(the filtered ones, or unfiltered). this is because a sortPosts action is embeded in the filterPosts action
    // filtered value will get passed to that sortPosts action function
    const filtered = who !== null && identity !== null;
    if (!selected && (who || identity)) {
      dispatch(removeFilter(filter, name, filtered, sort));
    } else if (selected) {
      dispatch(addFilter(filter, name, true, sort));
    }
  }, [selected]);

  return (
    <div
      className={selected ? styles.FilterOptionSelected : styles.FilterOption}
      onClick={() => selectHandler(filter, name)}
    >
      {props.filter}
    </div>
  );
};

export default FilterOption;
