import React, { Suspense } from "react";
import { Route, Switch } from "react-router-dom";
import { AnimatedSwitch } from "react-router-transition";
import Spinner from "../../components/UI/Spinners/FormSpinner/FormSpinner";
import ContentContainer from "../ContentContainer/ContentContainer";
import styles from "./Cockpit.module.css";

const Landing = React.lazy(() => import("../../components/Landing/Landing"));
const FullPost = React.lazy(() => import("../../components/FullPost/FullPost"));

const mapStyles = (styles) => {
  return {
    opacity: styles.opacity,
    transform: `scale(${styles.offset})`,
  };
};

const animatedTransition = {
  atEnter: {
    opacity: 0,
    offset: -100,
  },
  // leave in a transparent, downscaled state
  atLeave: {
    opacity: 0,
    offset: -100,
  },
  // and rest at an opaque, normally-scaled state
  atActive: {
    opacity: 1,
    scale: 1,
  },
};

const Cockpit = (props) => {
  let cockpitBody = (
    <Suspense fallback={<Spinner />}>
      <AnimatedSwitch
        atEnter={animatedTransition.atEnter}
        atLeave={animatedTransition.atLeave}
        atActive={animatedTransition.atActive}
        mapStyles={mapStyles}
        className={styles.Cockpit}
      >
        <Route path="/post/:id">
          <ContentContainer>
            <FullPost {...props} />
          </ContentContainer>
        </Route>
        <Route exact path="/">
          <Landing {...props} />
        </Route>
      </AnimatedSwitch>
    </Suspense>
  );
  //Landing
  //Resources
  //About
  //Post

  return cockpitBody;
};

export default Cockpit;
