import React, { useState } from "react";
import MessageSnapshot from "../../components/MessageSnapshot/MessageSnapshot";

const LoadMore = (props) => {
  const [messagesCutoff, setMessagesCutoff] = useState(14);
  const [filteredCutoff, setFilteredCutoff] = useState(14);

  let loadedMessages = null;
  let loadMoreMessages = null;
  let loadMessages = null;

  if (props.filteredLength > 14) {
    loadMoreMessages = (count) => {
      const marker =
        props.filteredLength - filteredCutoff >= count
          ? count
          : props.filteredLength - filteredCutoff;
      setFilteredCutoff((prevState) => prevState + marker);
    };

    loadMessages =
      props.filteredMessages.length - props.filteredCutoff > 0 &&
      props.filteredLength > 14 ? (
        <div onClick={() => loadMoreMessages(14)}>LOAD MORE</div>
      ) : null;

    loadedMessages = props.filteredMessages
      .slice(15, filteredCutoff)
      .map((message, index) => {
        return (
          <MessageSnapshot
            key={index}
            entry={message}
            marker={index}
          ></MessageSnapshot>
        );
      });
  } else {
    console.log("hello");

    loadMoreMessages = (count) => {
      const marker =
        props.messagesLength - messagesCutoff >= count
          ? count
          : props.messagesLength - messagesCutoff;
      setMessagesCutoff((prevState) => prevState + marker);
    };

    loadMessages =
      props.messagesLength - messagesCutoff > 0 && props.messagesLength > 14 ? (
        <div onClick={() => loadMoreMessages(14)}>LOAD MORE</div>
      ) : null;

    loadedMessages = props.messages
      .slice(15, filteredCutoff)
      .map((message, index) => {
        return (
          <MessageSnapshot
            key={index}
            entry={message}
            marker={index}
          ></MessageSnapshot>
        );
      });
  }

  return (
    <>
      {loadedMessages}
      {loadMessages}
    </>
  );
};

export default LoadMore;
