import React, { useState, useEffect } from "react";
import styles from "./Messages.module.css";
import { useSelector } from "react-redux";
import MessageSnapshot from "../../components/MessageSnapshot/MessageSnapshot";
import MessageFilter from "../MessageFilter/MessageFilter";
import Spinner from "../../components/UI/Spinners/FormSpinner/FormSpinner";

const Messages = () => {
  const messages = useSelector((state) => state.messages.messages);

  const { who, identity } = useSelector((state) => state.messages.filters);
  const sort = useSelector((state) => state.messages.sort);
  const filteredMessages = useSelector(
    (state) => state.messages.filteredMessages
  );

  const messagesLoading = useSelector((state) => state.messages.loading);

  const loadMoreMessages = (count) => {
    const marker =
      messages.length - messagesCutoff >= count
        ? count
        : messages.length - messagesCutoff;
    setMessagesCutoff((prevState) => prevState + marker);
  };

  const loadMoreFiltered = (count) => {
    const marker =
      filteredMessages.length - filteredCutoff >= count
        ? count
        : filteredMessages.length - filteredCutoff;
    setFilteredCutoff((prevState) => prevState + marker);
  };

  let messageFilter = messages.length > 0 ? <MessageFilter /> : null;

  let messagesContainer = messagesLoading ? <Spinner /> : null;

  let loadMessages = null;

  let loadFiltered = null;

  if (who || identity || sort) {
    messagesContainer = filteredMessages
      .slice(0, filteredCutoff)
      .map((message, index) => {
        return (
          <MessageSnapshot
            key={index}
            entry={message}
            marker={index}
          ></MessageSnapshot>
        );
      });
    loadFiltered =
      filteredMessages.length - filteredCutoff > 0 ? (
        <div onClick={() => loadMoreFiltered(14)}>LOAD MORE</div>
      ) : null;
  } else {
    messagesContainer = messages
      .slice(0, messagesCutoff)
      .map((message, index) => {
        return (
          <MessageSnapshot
            key={index}
            entry={message}
            marker={index}
          ></MessageSnapshot>
        );
      });

    loadMessages =
      messages.length - messagesCutoff > 0 ? (
        <div onClick={() => loadMoreMessages(14)}>LOAD MORE</div>
      ) : null;
  }

  return (
    <>
      {messageFilter}
      <div className={styles.Messages}>{messagesContainer}</div>
    </>
  );
};

export default Messages;

