import * as actionTypes from "./actionTypes";
import keys from "../../config/keys";

export const submitPost = (post) => {
  return async (dispatch) => {
    try {
      dispatch(submitPostStart());
      const response = await fetch(`${keys.apiPath}/messages`, {
        method: "POST",
        mode: "cors",
        cache: "no-cache",
        credentials: "same-origin",
        headers: {
          "Content-Type": "application/json",
          // 'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: JSON.stringify(post),
      });
      const data = await response.json();
      dispatch(submitPostSuccess(data));
    } catch (err) {
      dispatch(submitPostFail(err));
    }
  };
};

export const submitPostStart = () => {
  return {
    type: actionTypes.SUBMIT_POST_START,
  };
};

export const submitPostSuccess = (post) => {
  return {
    type: actionTypes.SUBMIT_POST_SUCCESS,
    post,
  };
};

export const submitPostFail = (error) => {
  return {
    type: actionTypes.SUBMIT_POST_FAIL,
    error,
  };
};

export const submitPostReset = () => {
  return {
    type: actionTypes.SUBMIT_POST_RESET,
  };
};

export const setPost = (postId) => {
  return {
    type: actionTypes.FIND_POST,
    postId,
  };
};

export const resetPost = () => {
  return {
    type: actionTypes.RESET_POST,
  };
};
